mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName}, receiver_phone=#{receiverPhone}, DEMAILADDR=#{divyAddr}, dlvy_request=#{divyRequest}, dlvy_date=TO_DATE(#{divyDate})
WHERE TRAN_NO = #{tranNo}

mapper.select:
	  	SELECT *
	  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
	  					FROM		(	select tran_no, receiver_name, receiver_phone, tran_status_code, buyer_id, PROD_NO 
		         						from transaction 


           									where 
           									buyer_id=#{buyerId}
        							) inner_table 	

						  WHERE ROWNUM <= #{search.endRowNum}  )
			WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

  	SELECT COUNT(*)
  	FROM(	select tran_no, receiver_name, receiver_phone, tran_status_code, buyer_id, PROD_NO 
	        from transaction
			where buyer_id= #{buyerId} 
	) vt
